import React, { useState } from 'react';
import {
    StyleSheet,
    View,
    TextInput,
    Button,
    Modal
  } from 'react-native';

const Input = ({ placeholder, submit, isVisible = false, onCancel }) => {
    const [text, setText] = useState('');

    const inputHandler = (value) => {
        if (value) {
            setText(value);
        }
    };

    const onSubmit = (e) => {
        e.preventDefault();
        submit(text);
        setText('');
    }

    return (
        <Modal visible={isVisible}>
            <View style={styles.inputContainer}>
                <TextInput 
                        placeholder={placeholder}
                        style={styles.input}
                        onChangeText={inputHandler}
                        value={text}
                />
                <View>

                </View>
                <Button style={styles.cancel} title="Cancel" color="red" onPress={onCancel} />
                <Button title="Add" onPress={onSubmit} />
            </View>
        </Modal>
    );
};

const styles = StyleSheet.create({
    inputContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center'
    },
    input: {
        flex: 1,
        width: '80%',
        borderColor: 'black',
        borderWidth: 1,
        padding: 10,
        marginBottom: 10
    },
    btnConainer: {

    }
  });

export default Input;
