import React from 'react';
import {
    StyleSheet,
    View,
    FlatList,
    Button,
    Text
} from 'react-native';

const Toast = ({show, undoDelete}) => {    
    return show &&
        <View style={styles.toast}>
            <View style={styles.toastContainer}>
                <Text style={{color: '#fff'}}>Removed Successfully</Text>
                <Text style={{color: '#FFC726'}} onPress={undoDelete}>Undo</Text>
            </View>
        </View>
};

const styles = StyleSheet.create({
    toastContainer: {
      flex: 1,
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center'
    }
});

export default Toast;
