import React, { useState, useEffect } from 'react';
import {
  StyleSheet,
  View,
  FlatList,
  Button,
  Text
} from 'react-native';
import Item from './components/Item/Item';
import Input from './components/Input/Input';

export default function App() {
  const [goal, setGoal] = useState('');
  const [goals, setGoals] = useState([]);
  const [isAddMode, setAddMode] = useState(false);
  const [isToastVisible, setToastVisibility] = useState(true);
  const [undoTime, setUndoTime] = useState(null);

  const goalInputHandler = (text) => {
    if (text) {
      setGoal(text);
    }
  };

  const addGoal = () => {
    setAddMode(false);
    setGoals(currentGoals => [
        ...currentGoals,
        { key: Math.random().toString() , value: goal }
    ]);

  };

  const deleteGoal = key => {
    console.log(key);
    setToastVisibility(true);
    setUndoTime(setTimeout(() => {
      setToastVisibility(false);
      setGoals(currentGoals =>
        currentGoals.filter(goal => goal.key !== key));
      setUndoTime(null);
    }, 3000));
  };

  const undoDelete = () => {
    setToastVisibility(false);
    setUndoTime(clearTimeout(undoTime));
  }
  return (
    <View style={styles.screen}>
        <Button
          title="Add New Goal"
          onPress={() => setAddMode(true)}
        />
        <Input
            placeholder="Course Goal"
            value={goal}
            inputHandler={goalInputHandler}
            submit={addGoal}
            isVisible={isAddMode}
        />
        <FlatList
            data={goals}
            renderItem={itemData => (
                <Item
                  item={itemData.item}
                  press={() => deleteGoal(itemData.item.key)} />
            )}
        />
      {/* <ScrollView style={styles.goalItems}>
          {goals.map((goal, index) => (
            <View key={index} style={styles.goalItem}>
              <Text>{goal}</Text>
            </View>
          ))}
      </ScrollView> */}
        {isToastVisible && 
          <View style={styles.toast}>
              <View style={styles.toastContainer}>
                <Text style={{color: '#fff'}}>Removed Successfully</Text>
                <Text style={{color: '#FFC726'}} onPress={undoDelete}>Undo</Text>
              </View>
          </View>
        }
    </View>

  );
}

const styles = StyleSheet.create({
  screen: {
    marginTop: 50,
    marginHorizontal: 20,
    flex: 1
  },
  inputContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center'
  },
  input: {
    width: '80%',
    borderColor: 'black',
    borderWidth: 1,
    padding: 10
  },
  goalItems: {

  },
  toast: {
    width: '100%', 
    height: 40, 
    backgroundColor: '#4A5050', 
    justifyContent: 'center',
    position: 'absolute',
    bottom: 0,
    paddingHorizontal: ''
  },
  toastContainer: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center'
  }
});
